import data from "../data.json";
const infos = data.events;



export const allEvents = (state = infos, action) => {
  switch (action.type) {
    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id - 1]: {
          ...state[action.event.id - 1],
          locations: state[action.event.id - 1].locations.map((lo) =>
            lo.id === action.id ? { ...lo, satus: 'yours' } : lo
          ),
        },
      };
    default:
      return state;
  }
};
