import React from "react";
import { connect } from "react-redux";
import { signIn, signOut } from "../actions";

class GoogleAuth extends React.Component {
 
  componentDidMount() {
    console.log(5);
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "595857126140-chevcgsb4mg97ocmpha7tspn0b96targ.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          th
          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }
  onAuthChange = (isSignedIn) => {
    if(isSignedIn){
        this.props.signIn()
    }else{
        this.props.signOut()
    }
  };

  onSignIn = () => {
    this.auth.signIn();
  };
  onSignOut = () => {
    this.auth.signOut();
  };
  renderLogin() {
    if (this.state.isSignedIn === null) {
      return null;
    } else if (this.state.isSignedIn) {
      return (
        <button onClick={this.onSignOut} className="btn btn-brown">
          <i className="fa fa-google ml-2" />
          تسجيل خروج
        </button>
      );
    } else {
      return (
        <button onClick={this.onSignIn} className="btn btn-brown">
          <i className="fa fa-google ml-2" />
          تسجيل دخول
        </button>
      );
    }
  }
  render() {
    return <div>{this.renderLogin()}</div>;
  }
}

const mapStateToProps=(state)=>{
  return{
    isSignedIn:state.auth.isSignedIn
  }
}

export default connect(null, {
  signIn,
  signOut,
})(GoogleAuth);
