import React from 'react';
import {connect} from 'react-redux'
class EventDetails extends React.Component {
    render() { 
        const{title,img,video}=this.props.event

        return ( 
            <section className="details">

            <div className="container">
            <div className="row">
            <div className="col-md-6">
            <img src={img} alt=""/>
            </div>
            <div className="col-md-6">
            <iframe width="560" height="315" src="https://www.youtube.com/embed/4HHwJyBkYWE" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
            </div>
            </div></div>
            </section>
        );
    }
}
 const mapStateToProps = (state)=>{
    return{
        event:state.event
    }
 }
export default connect(mapStateToProps)(EventDetails);
