import data from '../data.json'
const infos= data.events


 export const allEvents = (state = [], action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return action.payload
    case 'SELECT_SEAT':
      return {
        id:4
      }
    default:
      return state;
  }
};
