import datas from '../data.json'
const infos= datas.events


 export const data = (state = infos, action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return [...action.payload];
    case "EVENT_INFO":{}

    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id - 1]: {
          ...state[action.event.id - 1],
          locations: state[action.event.id - 1].locations.map((lo) =>
            lo.id === action.id ? { ...lo, satus:5 } : lo
          ),
        },
      };
    default:
      return state;
  }
};
