const data = (state = [], action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return [...action.payload];
    case "SELECT_SEAT":
      return state.map((e) =>
        e.id === action.event.id ? { ...e,locations } : e
      );
    default:
      return state;
  }
};

export default data;
