import React from "react";
import { TwitterTimelineEmbed } from "react-twitter-embed";

const News = () => {
  return (
    <div className="container">
      <div className="row">
        <TwitterTimelineEmbed
          sourceType="profile"
          screenName="saradiqsa"
          options={{
            tweetLimit: "3",
            width: "100%",
            height: "",
          }}
          theme="light"
          noHeader="true"
          noBorders="true"
          noFooter="true"
        ></TwitterTimelineEmbed>
      </div>
    </div>
  );
};

export default News;
