import React from "react";

class GoogleAuth extends React.Component {
  state = {
    isSignedIn: null,
    signIn:null,
    signOut:null,
  };
  componentDidMount() {
      console.log(5)
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "595857126140-chevcgsb4mg97ocmpha7tspn0b96targ.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSignedIn: this.auth.isSignedIn.get(),signIn:null,
            signOut:null, });
          this.auth.isSignedIn.listen(this.onAuthChange)
        });
    });
  }
  onAuthChange=()=>{
      this.setState({isSignedIn:this.auth.isSignedIn.get()})
  }
  renderLogin() {
    if (this.state.isSignedIn === null) {
      return <button className="btn btn-brown"> لاأعلم</button>;
    } else if (this.state.isSignedIn) {
      return <button className="btn btn-brown">تسجيل الدخول</button>;
    } else {
      return <button className="btn btn-brown">تسجيل خوج</button>;
    }
  }
  render() {
    return <div>{this.renderLogin()}</div>;
  }
}

export default GoogleAuth;
