import React from "react";

class GoogleAuth extends React.Component {
  state = {
    isSignedIn: null,
    
  };
  componentDidMount() {
    console.log(5);
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "595857126140-chevcgsb4mg97ocmpha7tspn0b96targ.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({
            isSignedIn: this.auth.isSignedIn.get(),
            signIn: this.auth.signIn,
            signOut: this.auth.signOut,
          });
          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }
  onAuthChange = () => {
    this.setState({ isSignedIn: this.auth.isSignedIn.get() });
  };

onSignIn=()=>{
    this.auth.signIn
}
onSignOut=()=>{
    this.auth.signOut
}
  renderLogin() {
    if (this.state.isSignedIn === null) {
      return null;
    } else if (this.state.isSignedIn) {
      return (
        <button onClick={this.auth} className="btn btn-brown">
          <i className="fa fa-google ml-2" />
          تسجيل خروج
        </button>
      );
    } else {
      return (
        <button onClick={this.onSignIn} className="btn btn-brown">
          <i className="fa fa-google ml-2" />
          تسجيل دخول
        </button>
      );
    }
  }
  render() {
    return <div>{this.renderLogin()}</div>;
  }
}

export default GoogleAuth;
