import React from "react";

class GoogleAuth extends React.Component {
    state={
        isSingedIn:null
    }
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client.init({
        clientId:
          "595857126140-chevcgsb4mg97ocmpha7tspn0b96targ.apps.googleusercontent.com",
        scope: "email",
      }).then(()=>{
          this.auth=window.gapi.auth2.getAuthInstance()
          this.setState({isSingedIn:this.auth.isSingedIn.get()})
      });
    });
  }

  renderLogin(){
      if(this.state.isSingedIn===nul){
          return <p>Don't know</p>
      }
  }
  render() {
      console.log(this.state.isSingedIn)
    return <button className="btn btn-brown">تسجيل الدخول</button>;
  }
}

export default GoogleAuth;
