import data from "../data.json";
import { bindActionCreators } from "redux";
const initialState = {
  id: "1",
  price: "2000",
  feature: "واجهتين",
  satus: "available",
};

export const allData = (state = data.events, action) => {
  console.log(state);
  switch (action.type) {
    case "SELECT_SEAT":
      const { id } = action.event;
      return state.map((event) =>
        event.id === id ? event.locations.map((location) => {
          location.id===action.id?lo:location
        }) : event
      );
    default:
      return state;
  }
};

export const event = (state = null, action) => {
  switch (action.type) {
    case "EVENT_INFO":
      return action.payload;
    default:
      return state;
  }
};
