 const data = (state=[],action)=>{
    switch(action.type){
        case 'FETCH_DATA':return [...action.payload]
        case 'SELECT_SEAT':return [...state,action.event.locations.map((lo)=>{return {...lo,satus:4}})]
        default:
            return state
    }
}

export default data
