const event = (state = {}, action) => {
  switch (action.type) {
    case "EVENT_INFO":
      return { ...action.payload };
    case "SELECT_SEAT":
      return { ...state, locations: state.locations.map(location=>location.id===acti) };

    default:
      return state;
  }
};

export default event;
