const data = (state = [], action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return [...action.payload];
    case "SELECT_SEAT":
      return {
        ...state,
        locations: state.locations.map((e) =>
          e.id === action.id ? { ...locations, satus: "busy" } : "none"
        ),
      };
    default:
      return state;
  }
};

export default data;
