const data = (state = [], action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return [...action.payload];
    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id - 1]: {
          ...state[action.event.id - 1],
          locations: state[action.event.id - 1].locations.map((lo) =>
            lo.id === action.id ? { ...lo, satus: "busy" } : lo
          ),
        },
      };
    default:
      return state;
  }
};

export default data;
