import data from "../data.json";
const infos = data.events;

export const event = (state = null, action) => {
  switch (action.type) {
    case "EVENT_INFO":
      return action.payload;
    case "SELECT_SEAT":
      return {
        ...state,
        locations: state.locations.map((lo) =>
          lo.id === action.id ? { ...lo, satus: 'yours' } : lo
        ),
      };
    default:
      return state;
  }
};

export const allEvents = (state = infos, action) => {
  switch (action.type) {
    case "SELECT_SEAT":
      return {
        ...state,
        [action.id-1]:state
       
      };
    default:
      return state;
  }
};



