import data from "../data.json";
const infos = data.events;

export const event = (state = null, action) => {
  switch (action.type) {
    case "EVENT_INFO":
      return action.payload;
    case "SELECT_SEAT":
      return {
        ...action.event,
        event: action.event.locations.map((lo) =>
          lo.id === action.id ? lo.satus='yours' : lo.satus
        ),
      };
    default:
      return state;
  }
};

export const allEvents = (state = infos, action) => {
  switch (action.type) {
    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id - 1]: {
          ...state[action.event.id - 1],
          locations: state[action.event.id - 1].locations.map((lo) =>
            lo.id === action.id ? lo.satus : lo
          ),
        },
      };
    default:
      return state;
  }
};
