import data from "../data.json";
const infos = data.events;

export const event = (state = null, action) => {
  const payload = action.payload;
  const e = infos[payload - 1];
  switch (action.type) {
    case "EVENT_INFO":
      return {
        ...state,
        locations: state.locations.map((lo) =>
          e.id === payload ? { ...lo, satus: '' } : 8
        ),
      };
    default:
      return state;
  }
};

export const allEvents = (state = infos, action) => {
  switch (action.type) {
    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id - 1]: {
          ...state[action.event.id - 1],
          locations: state[action.event.id - 1].locations.map((lo) =>
            lo.id === action.id ? { ...lo, satus: 2020 } : lo
          ),
        },
      };
    default:
      return state;
  }
};
