const data = (state = [], action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return [...action.payload];
    case "SELECT_SEAT":
      return {
        ...state,
        [action.event.id-1]: {...state}
        
      };
    default:
      return state;
  }
};

export default data;
