import React from "react";
import CountUp from "react-countup";
import ads from "../images/promotion.png";
import event from "../images/calendar.svg";
import apps from "../images/selfie.svg";
import site from "../images/3.svg";

class Statistics extends React.Component {
  /* state = {
    counter: 500,
    eventsCounter:100,
  };

  increasecounter = () => {
    this.setState({ counter: 0 });
    const inter = setInterval(() => {
      if (this.state.counter <||this.state.eventsCounter < 50) {
        this.setState({ counter: this.state.counter + 1 });
      } else if (this.state.counter === 50) {
        this.setState({ counter: 50 });
        this.stopInteral(inter);
      }
    }, 30);
  };
  continue = () => {
    const inter = setInterval(() => {
      this.setState({
        counter: this.state.counter,
      });
      if (this.state.counter === 50) {
        this.setState({ counter: 50 });
        this.stopInteral(inter);
      }
    }, 30);
  };
  stopInteral = (inter) => {
    clearInterval(inter);
  }; */
  render() {
    return (
      <section className="statistics">
        <div className="container">
          <h2 className="heading-secondary u-margin-bottom">إحصائيات</h2>
          <div className="row statistics__card-container ">
            <div className="col-md-4">
              <div
                className="card statistics__card"
                /* onMouseOver={this.increasecounter}
                onMouseLeave={this.continue}
                onScroll={this.increasecounter} */
              >
                <div className="statistics__card-img">
                  <img
                    className="card-img-top"
                    src={ads}
                    alt="Card image cap"
                  />
                </div>

                <div className="card-body statistics__card-body">
                  <h1 class="card-title">100</h1>
                  <p className="card-text">حملة إعلانية</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card statistics__card"
           
              >
                <div className="statistics__card-img">
                  <img
                    className="card-img-top"
                    src={e}
                    alt="Card image cap"
                  />
                </div>
                <div className="card-body statistics__card-body">
                  <h1 class="card-title">500</h1>
                  <p className="card-text">فعالية</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card statistics__card">
              <div className="statistics__card-img">
              <img
                className="card-img-top"
                src={apps}
                alt="Card image cap"
              />
            </div>
                <div className="card-body statistics__card-body">
                  <h1 class="card-title">5</h1>
                  <p className="card-text">تطبيق جوال</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Statistics;
