 const data = (state=[],action)=>{
    switch(action.type){
        case 'FETCH_DATA':return [...action.payload]
        case 'SELECT_SEAT':return [...state,action.event.locations.map((lo)=>[...lo,lo.id===act])]
        default:
            return state
    }
}

export default data
