import React from "react";
import { connect } from "react-redux";
import {selectSeat} from '../actions'
class Planing extends React.Component {
  state = {};
  
  render() {
    const {id, locations } = this.props;
    console.log(this.props)
    return (
      <div
        className="planing d-flex flex-wrap "
        style={{ width: "200px" }}
      >
        {locations.map((location) => {
          return (
            <div  onClick={()=>console.log(location)}  className='planing__icon-box mb-2' style={{ width: "33.333px" }}>
              <i className={`planing__icon-${location.satus} fa fa-user fa-lg`}></i>
            </div>
          );
        })}
      </div>
    );
  }
}
const maStateToProps = (state) => {
  return {
    locations: state.event.locations,
  };
};

export default connect(maStateToProps,{
    selectSeat
})(Planing);
